function levenshtein(a, b) {
  const matrix = [];
  for (let i = 0; i <= b.length; i++) { matrix[i] = [i]; }
  for (let j = 0; j <= a.length; j++) { matrix[0][j] = j; }
  for (let i = 1; i <= b.length; i++) {
    for (let j = 1; j <= a.length; j++) {
      const cost = b[i - 1] === a[j - 1] ? 0 : 1;
      matrix[i][j] = Math.min(
        matrix[i - 1][j] + 1,
        matrix[i][j - 1] + 1,
        matrix[i - 1][j - 1] + cost
      );
    }
  }
  return matrix[b.length][a.length];
}
// Usage: Match closest coffee name
const input = "capachino";
const coffeeOptions = ["cappuccino", "latte", "drip", "macchiato"];
const bestMatch = coffeeOptions.reduce((best, name) => {
  const score = levenshtein(input.toLowerCase(), name.toLowerCase());
  return score < best.score ? { name, score } : best;
}, { name: null, score: Infinity });
console.log(`Best match for "${input}": ${bestMatch.name} (score: ${bestMatch.score})`);
